const subscriptionCreateAndPayQuery =
    "\nmutation subscriptionCreateAndPay (\$input: SubscriptionInput!, \$payWayCode:PayWayCode!,\$projectName:ProjectNameEnum!){\n    subscriptionCreateAndPay(input: \$input,payWayCode:\$payWayCode,projectName:\$projectName) {\n        subscription {\n            id\n            no\n        }\n        paymentStartResult {\n            isSuccess\n            payment {\n                amount\n                currency\n                consumerAccountOpenId\n                consumerId\n                details{\n                    code\n                    id\n                    paymentId\n                    value\n                }\n                id\n                paymentFinishTime\n                paymentStartTime\n                payWayCode\n                payWayId\n                payWayOrderId\n                payWayPrePayId\n                status\n                orderId\n                orderNo\n            }\n            wxPaymentRequest {\n                nonceStr\n                package\n                paySign\n                signType\n                timeStamp\n            }\n            aliPaymentRequest {\n                payWayOrderId\n                orderStr\n            }\n        }\n    }\n  }\n";
